import React,{useState,useEffect} from 'react';
import './weatherdisplay.css';
const CallApi = () => {
    const [data, setdata] = useState(null);
const [search, setsearch] = useState("Mumbai")

useEffect(() => {
    const fetchApi = async() =>{
        const url =`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=4e7fa739785a440e6ca8e2e9b4748236`;
        const response = await fetch(url);
        const resJson = await response.json();
        setdata(resJson.main);
        console.log(resJson.main);
       };
       fetchApi();
},[search])

    return (<>
        <div class="weather-side">
        <input type="text" class="form-control" onChange={(event) =>{setsearch(event.target.value)}}></input>
        <div >
            {
               !data?(<h1 >No data</h1>):(<><h1 class="location">{search}</h1><h2 class="weather-temp">{data.temp}</h2></>)
            }
        </div>
        </div>
        </>
    )
}

export default CallApi;
