const [search, setsearch] = useState("Mumbai")

useEffect(() => {
    const fetchApi = async() =>{
        const url =`http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=4e7fa739785a440e6ca8e2e9b4748236`;
        const response = await fetch(url);
        const resJson = await response.json();
        setdata(resJson);
        console.log(resJson);
       };
       fetchApi();
})

    return (<>
        <input onChange={(event) =>{setsearch(event.target.value)}}></input>
        <div>
            {
               !data?(<p>No data</p>):(<><p>{data.name}</p><p>{data.main.temp}</p></>)
            }
        </div>
        </>
    )
}

